const express = require('express');
const app = express();
const port = 3000;

// Middleware untuk membaca JSON
app.use(express.json());

// Route GET (untuk mengambil data)
app.get('/', (req, res) => {
  res.send('Halo! Ini backend sederhana dengan Express.');
});

// Route GET dengan response JSON
app.get('/api/users', (req, res) => {
  res.json([
    { id: 1, nama: 'Budi' },
    { id: 2, nama: 'Sinta' }
  ]);
});

// Route POST (untuk mengirim data)
app.post('/api/users', (req, res) => {
  const userBaru = req.body;
  res.json({
    message: 'User berhasil ditambahkan',
    data: userBaru
  });
});

// Jalankan server
app.listen(port, () => {
  console.log(`Server berjalan di http://localhost:${3370}`);
});
